import { Flex, Avatar, Text, Button } from "@chakra-ui/react";

const Notification = ({ toast }) => {
  return (
    <Flex
      w="100%"
      bg="brand.white"
      borderRadius="md"
      justifyContent="space-between"
      p={2}
      align="center"
    >
      <Flex align="center">
        <Avatar size="sm" mr={2} />
        <Text fontSize="lg">Joe Bloggs upvoted your post</Text>
      </Flex>

      <Text>Fri 25th Dec 2020, 15:22</Text>
    </Flex>
  );
};

export default Notification;
