import {
  Box,
  Avatar,
  Heading,
  Text,
  Flex,
  Stack,
  HStack,
  IconButton,
} from "@chakra-ui/react";

const Post = () => {
  return (
    <Stack
      w="100%"
      h="100px"
      bg="tomato"
      boxShadow="sm"
      p={4}
      borderRadius="md"
    >
      <Flex align="center">
        <Avatar size="sm" mr={2} />
        <Heading size="md">Freddie Wellfair</Heading>
      </Flex>
      <Text>This is an example of the body of a post</Text>

      <HStack>
        <IconButton />
        <IconButton />
        <IconButton />
      </HStack>
    </Stack>
  );
};

export default Post;
