import { ArrowDownIcon, ArrowUpIcon, ChatIcon } from "@chakra-ui/icons";
import {
  Box,
  Avatar,
  Heading,
  Text,
  Flex,
  Stack,
  HStack,
  Button,
  Divider,
  Input,
} from "@chakra-ui/react";
import { useState } from "react";

const Post = () => {
  const [open, setOpen] = useState(true);

  return (
    <Stack
      w="100%"
      bg="tomato"
      boxShadow="sm"
      p={4}
      borderRadius="md"
      spacing={4}
    >
      <Flex align="center">
        <Avatar size="sm" mr={2} />
        <Heading size="md">Freddie Wellfair</Heading>
      </Flex>
      <Text fontSize="lg">This is an example of the body of a post</Text>

      <Box>
        <Divider my={2} />
        <HStack w="100%" justify="space-between">
          <Button w="32%" bg="none">
            <ArrowUpIcon mr={1} /> 170
          </Button>
          <Button w="32%" bg="none">
            <ChatIcon mr={1} /> 120
          </Button>
          <Button w="32%" bg="none">
            <ArrowDownIcon mr={1} /> 100
          </Button>
        </HStack>
        {open ? (
          <Stack>
            <Flex align="center">
              <Avatar size="sm" mr={2} />
              <Input h="32px" />
            </Flex>
          </Stack>
        ) : null}
      </Box>
    </Stack>
  );
};

export default Post;
