import {
  Flex,
  Input,
  Stack,
  Button,
  Text,
  Box,
  IconButton,
} from "@chakra-ui/react";
import { ArrowBackIcon } from "@chakra-ui/icons";
import { useState } from "react";

const Login = () => {
  const [showLogin, setShowLogin] = useState(true);

  return (
    <Flex justify="center" align="center" h="calc(100vh - 60px)">
      <Stack
        spacing={3}
        justify="center"
        borderRadius="md"
        bg="tomato"
        w="xs"
        h="xs"
        align="center"
      >
        {showLogin ? (
          <>
            <Input bg="white" variant="outline" placeholder="Email" />
            <Input bg="white" variant="outline" placeholder="Password" />
            <Button w="100%">Login</Button>
            <Text>
              Not registered?{" "}
              <Box
                onClick={() => setShowLogin(!showLogin)}
                as="span"
                color="gray.100"
                _hover={{ cursor: "pointer" }}
              >
                Create an account
              </Box>{" "}
            </Text>
          </>
        ) : (
          <>
            <IconButton
              onClick={() => setShowLogin(!showLogin)}
              aria-label="Back"
              bg="none"
              icon={<ArrowBackIcon w="30px" h="30px" />}
            />
            <Input bg="white" variant="outline" placeholder="Email" />
            <Input bg="white" variant="outline" placeholder="Username" />
            <Input bg="white" variant="outline" placeholder="Password" />
            <Button w="100%">Create Account</Button>
          </>
        )}
      </Stack>
    </Flex>
  );
};

export default Login;
