import {
  Flex,
  Input,
  Stack,
  Button,
  Text,
  Box,
  useDisclosure,
  Fade,
} from "@chakra-ui/react";
import { useState } from "react";

const Login = () => {
  const [showLogin, setShowLogin] = useState(true);
  const { isOpen, onToggle } = useDisclosure();

  const toggle = () => {
    setShowLogin(!showLogin);
    onToggle();
  };

  return (
    <Flex justify="center" align="center" h="calc(100vh - 60px)">
      <Stack
        justify="center"
        borderRadius="md"
        bg="tomato"
        w="xs"
        h="xs"
        px={7}
        py={10}
        spacing={3}
        boxShadow="md"
      >
        {showLogin ? (
          <>
            <Input bg="white" variant="outline" placeholder="Email" />
            <Input bg="white" variant="outline" placeholder="Password" />
            <Button w="100%">Login</Button>
            <Text>
              Not registered?{" "}
              <Box
                onClick={toggle}
                as="span"
                color="gray.100"
                _hover={{ cursor: "pointer" }}
              >
                Create an account
              </Box>{" "}
            </Text>
          </>
        ) : (
          <>
            <Fade in={isOpen}>
              <Input bg="white" variant="outline" placeholder="Email" />
              <Input bg="white" variant="outline" placeholder="Username" />
              <Input bg="white" variant="outline" placeholder="Password" />
              <Button w="100%">Create Account</Button>
              <Button w="100%" onClick={toggle}>
                Cancel
              </Button>
            </Fade>
          </>
        )}
      </Stack>
    </Flex>
  );
};

export default Login;
