import { Box, Avatar, Heading, Text, Flex, Stack } from "@chakra-ui/react";

const Post = () => {
  return (
    <Stack
      w="100%"
      h="100px"
      bg="tomato"
      boxShadow="sm"
      p={2}
      borderRadius="md"
    >
      <Flex align="center">
        <Avatar size="sm" mr={2} />
        <Heading size="md">Freddie Wellfair</Heading>
      </Flex>
      <Text>This is an example of the body of a post</Text>
    </Stack>
  );
};

export default Post;
