import {
  Box,
  Avatar,
  Heading,
  Text,
  Flex,
  Stack,
  HStack,
  IconButton,
  Divider,
} from "@chakra-ui/react";

const Post = () => {
  return (
    <Stack w="100%" bg="tomato" boxShadow="sm" p={4} borderRadius="md">
      <Flex align="center">
        <Avatar size="sm" mr={2} />
        <Heading size="md">Freddie Wellfair</Heading>
      </Flex>
      <Text>This is an example of the body of a post</Text>

      <Box>
        <Divider />
        <HStack w="100%">
          <IconButton w="30%" />
          <IconButton w="30%" />
          <IconButton w="30%" />
        </HStack>
      </Box>
    </Stack>
  );
};

export default Post;
