import { HamburgerIcon } from "@chakra-ui/icons";
import {
  Avatar,
  Box,
  Flex,
  IconButton,
  Drawer,
  DrawerBody,
  DrawerFooter,
  DrawerHeader,
  DrawerOverlay,
  DrawerContent,
  DrawerCloseButton,
} from "@chakra-ui/react";

const Header = () => {
  return (
    <Flex
      w="100%"
      h="60px"
      bg="tomato"
      align="center"
      justify="space-between"
      px={4}
    >
      <IconButton
        bg="none"
        aria-label="Menu"
        icon={<HamburgerIcon w="25px" h="25px" />}
      />

      <Avatar size="sm" />
    </Flex>
  );
};

export default Header;
