import { Box, Avatar, Heading, Text, Flex } from "@chakra-ui/react";

const Post = () => {
  return (
    <Box w="100%" h="100px" bg="tomato" boxShadow="sm" p={2} borderRadius="md">
      <Flex align="center">
        <Avatar size="sm" />
        <Heading size="md">Freddie Wellfair</Heading>
      </Flex>
    </Box>
  );
};

export default Post;
