import { Flex, Avatar, Text, Button } from "@chakra-ui/react";

const Notification = ({ toast }) => {
  return (
    <Flex
      w="100%"
      bg="brand.white"
      borderRadius="md"
      justifyContent="space-between"
      p={2}
      align="center"
    >
      <Flex align="center">
        <Avatar size="xs" mr={2} />
        <Text fontSize="sm">Joe Bloggs upvoted your post</Text>
      </Flex>

      <Text fontSize="sm" textAlign="right">
        25/12/20, 15:22
      </Text>
    </Flex>
  );
};

export default Notification;
