import {
  Box,
  Avatar,
  Heading,
  Text,
  Flex,
  Stack,
  HStack,
  Button,
  Divider,
} from "@chakra-ui/react";

const Post = () => {
  return (
    <Stack
      w="100%"
      bg="tomato"
      boxShadow="sm"
      p={4}
      borderRadius="md"
      spacing={4}
    >
      <Flex align="center">
        <Avatar size="sm" mr={2} />
        <Heading size="md">Freddie Wellfair</Heading>
      </Flex>
      <Text fontSize="lg">This is an example of the body of a post</Text>

      <Box>
        <Divider my={2} />
        <HStack w="100%" justify="space-between">
          <Button w="32%">Up</Button>
          <Button w="32%">Down</Button>
          <Button w="32%">Share</Button>
        </HStack>
      </Box>
    </Stack>
  );
};

export default Post;
